      { name: "Ä°ngiliz Sterlini", code: "GBP", rate: "42.80", sellRate: "40.20", logoUrl: "https://images.unsplash.com/photo-1513475382585-d06e58bcb0e0?ixlib=rb-4.0.3&auto=format&fit=crop&w=64&h=64" },
      { name: "Japon Yeni", code: "JPY", rate: "0.24", sellRate: "0.22", logoUrl: "https://images.unsplash.com/photo-1540959733332-eab4deabeeaf?ixlib=rb-4.0.3&auto=format&fit=crop&w=64&h=64" },
      { name: "Kanada DolarÄ±", code: "CAD", rate: "25.40", sellRate: "23.80", logoUrl: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?ixlib=rb-4.0.3&auto=format&fit=crop&w=64&h=64" }
