    // Initialize teams
    const initialTeams: InsertTeam[] = [
      { name: "1 TakÄ±mÄ±", cashBalance: "100000.00", accessCode: "00012024", profilePicUrl: null },
      { name: "2 TakÄ±mÄ±", cashBalance: "100000.00", accessCode: "00022024", profilePicUrl: null },
      { name: "3 TakÄ±mÄ±", cashBalance: "100000.00", accessCode: "00032024", profilePicUrl: null },
