import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Plus, Minus, DollarSign, TrendingUp, TrendingDown } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { type Team } from "@shared/schema";

export function FinancialOverview() {
  const [selectedTeam, setSelectedTeam] = useState<Team | null>(null);
  const [adjustmentAmount, setAdjustmentAmount] = useState("");
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [adjustmentType, setAdjustmentType] = useState<"add" | "subtract">("add");
  const { toast } = useToast();

  const { data: teams, isLoading } = useQuery<Team[]>({
    queryKey: ["/api/teams"],
  });

  const adjustCashMutation = useMutation({
    mutationFn: async ({ teamId, amount, type }: { teamId: number; amount: number; type: "add" | "subtract" }) => {
      const team = teams?.find(t => t.id === teamId);
      if (!team) throw new Error("Team not found");

      const currentBalance = parseFloat(team.cashBalance);
      const newBalance = type === "add" ? currentBalance + amount : currentBalance - amount;

      if (newBalance < 0) {
        throw new Error("Insufficient funds");
      }

      const response = await fetch(`/api/teams/${teamId}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ cashBalance: newBalance.toFixed(2) }),
      });
      if (!response.ok) throw new Error("Failed to update balance");
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/teams"] });
      toast({ title: "Nakit bakiye gÃ¼ncellendi" });
      setIsDialogOpen(false);
      setAdjustmentAmount("");
      setSelectedTeam(null);
    },
    onError: (error: Error) => {
      toast({ 
        title: error.message === "Insufficient funds" ? "Yetersiz bakiye" : "GÃ¼ncelleme baÅŸarÄ±sÄ±z", 
        variant: "destructive" 
      });
    },
  });

  const handleAdjustment = (team: Team, type: "add" | "subtract") => {
    setSelectedTeam(team);
    setAdjustmentType(type);
    setIsDialogOpen(true);
  };

  const onSubmit = () => {
    if (!selectedTeam || !adjustmentAmount) return;

    const amount = parseFloat(adjustmentAmount);
    if (isNaN(amount) || amount <= 0) {
      toast({ title: "GeÃ§erli bir miktar girin", variant: "destructive" });
      return;
    }

    adjustCashMutation.mutate({
      teamId: selectedTeam.id,
      amount,
      type: adjustmentType,
    });
  };

  const calculateNetProfit = (team: Team) => {
    const currentBalance = parseFloat(team.cashBalance);
    const initialBalance = 100000; // Initial starting balance
    return currentBalance - initialBalance;
  };

  if (isLoading) {
    return <div>TakÄ±mlar yÃ¼kleniyor...</div>;
  }

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h3 className="text-lg font-semibold">Mali Durum Ã–zeti</h3>
      </div>

      <div className="grid gap-4">
        {teams?.map((team) => {
          const netProfit = calculateNetProfit(team);
          const isProfit = netProfit >= 0;

          return (
            <Card key={team.id}>
              <CardHeader>
                <CardTitle className="flex items-center justify-between">
                  <div className="flex items-center gap-3">
                    <span>{team.name}</span>
                    <div className={`flex items-center gap-1 text-sm ${
                      isProfit ? "text-green-600" : "text-red-600"
                    }`}>
                      {isProfit ? <TrendingUp className="h-4 w-4" /> : <TrendingDown className="h-4 w-4" />}
                      {isProfit ? "Kar" : "Zarar"}: â‚º{Math.abs(netProfit).toLocaleString('tr-TR', { minimumFractionDigits: 2 })}
                    </div>
                  </div>
                  <div className="flex space-x-2">
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleAdjustment(team, "add")}
                      className="text-green-600 hover:text-green-700"
                    >
                      <Plus className="h-4 w-4 mr-1" />
                      Para Ekle
                    </Button>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => handleAdjustment(team, "subtract")}
                      className="text-red-600 hover:text-red-700"
                    >
                      <Minus className="h-4 w-4 mr-1" />
                      Para Ã‡Ä±kar
                    </Button>
                  </div>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-3 gap-4 text-sm">
                  <div>
                    <p className="text-muted-foreground">Mevcut Bakiye</p>
                    <p className="font-medium text-lg">
                      â‚º{parseFloat(team.cashBalance).toLocaleString('tr-TR', { minimumFractionDigits: 2 })}
                    </p>
                  </div>
                  <div>
                    <p className="text-muted-foreground">BaÅŸlangÄ±Ã§ Bakiyesi</p>
                    <p className="font-medium">â‚º100.000,00</p>
                  </div>
                  <div>
                    <p className="text-muted-foreground">EriÅŸim Kodu</p>
                    <p className="font-medium font-mono">{team.accessCode}</p>
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>

      <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle>
              {selectedTeam?.name} - {adjustmentType === "add" ? "Para Ekle" : "Para Ã‡Ä±kar"}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div>
              <Label htmlFor="amount">Miktar (â‚º)</Label>
              <Input
                id="amount"
                type="number"
                step="0.01"
                placeholder="0.00"
                value={adjustmentAmount}
                onChange={(e) => setAdjustmentAmount(e.target.value)}
              />
            </div>

            <div className="text-sm text-muted-foreground">
              Mevcut bakiye: â‚º{selectedTeam ? parseFloat(selectedTeam.cashBalance).toLocaleString('tr-TR', { minimumFractionDigits: 2 }) : "0,00"}
            </div>

            <div className="flex space-x-2">
              <Button 
                onClick={onSubmit} 
