    resolver: zodResolver(teamCurrencyFormSchema),
    defaultValues: {
      teamId: 0,
      currencyId: 0,
      amount: "",
    },
  });

  const startupForm = useForm({
    resolver: zodResolver(teamStartupFormSchema),
